%============================================================%  BIKEPRE   Data for vehicle handling model%%------------------------------------------------------------% ... constantsa=1.0;		% distance from front wheel to C.G of vehicleb=1.69;		% distance from rear wheel to C.G of vehicleCf=60530;	% front cornering stiffness [N/rad]Cr=64656;	% rear cornering stiffness [N/rad]M=1552;		% vehicle mass [kg]I=2640;		% vehicle moment of inertia [kg/m^2]G=1;		% gear ratio%c=120;		% logitudinal force [N]% initial conditionsu0=10;	% vehicle longitudinal velocity [m/s]r0=0;		% vehicle lateral velocity [m/s]v0=0;		% vehicle yaw rate [rad/s]theta0=pi/180;	% 1 degree in radiantheta=theta0*20;	% steering wheel inputc_values_array = [0, 50, 75, 100, 125, 150, 200]; % Acceleration magnitudes%theta_values_array = pi/180 * [2, 4, 6, 8, 10];  % Steering magnitudes in radianstheta_values_array = (1:0.1:10.1) * pi/180;